mapper.update:
UPDATE assignment
SET status = 'ASSIGNED',
    repair_person_id = #{repair_person_id}
WHERE assignment_id = #{assignment_id}

UPDATE assignment
  SET status = 'PENDING',repair_person_id = null
  WHERE assignment_id = #{assignment_id}

UPDATE assignment
SET status = 'IN_PROGRESS'
WHERE assignment_id = #{assignment_id}

UPDATE assignment
SET status = 'COMPLETED'
WHERE assignment_id = #{assignment_id}
